+  private static Thread acquireFor(final Lock lock, final long duration, final TimeUnit unit) {
+    final CountDownLatch latch = new CountDownLatch(1);
+    Thread thread =
+        new Thread() {
+          @Override
+          public void run() {
+            lock.lock();
+            latch.countDown();
